        if {not [info exists tmm_auth_http_sids(krbdelegate)] or \
            ($tmm_auth_http_sids(krbdelegate) != [AUTH::last_event_session_id]) or \
            (not [info exists tmm_auth_http_collect_count])} {
            return
        }
        if {[AUTH::status] == 0} {
